	Circle2d.instance = (radius, center) => {
	  const inst = Lookup.instance(Circle2d.name);
	  inst.radius(radius);
	  inst.center(center);
	  return inst;
	}
	new Circle2d();
	
	module.exports = Circle2d;
	
